VALUES ('B1_U49_B2_I3', 'ingles-b1-U49', 4, 'multiple_choice', 'Choose the correct option to complete each sentence in the past continuous tense.', '{"prompt_es":"Choose the correct option to complete each sentence in the past continuous tense.","mastery_tag":"grammar:b1-past-continuous","explanation":"Se usa ''was'' porque la acción estaba en progreso en el pasado.","stimulus_en":"I saw him while he ___ (give) a presentation.","options":[{"id":"o1","text":"was giving"},{"id":"o2","text":"were giving"},{"id":"o3","text":"is giving"},{"id":"o4","text":"gave"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","past","continuous","unit"]}')
