INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U53_B3_I2', 'ingles-b1-U53', 12, 'multiple_choice', 'Choose the correct option to complete each sentence in passive voice.', '{"prompt_es":"Choose the correct option to complete each sentence in passive voice.","mastery_tag":"grammar:b1-passive","explanation":"Aquí también se usa ''were'' porque la acción ocurrió en el pasado y se trata de la voz pasiva.","stimulus_en":"The windows ___ last week.","options":[{"id":"o1","text":"were cleaned"},{"id":"o2","text":"cleaned"},{"id":"o3","text":"are cleaning"},{"id":"o4","text":"was clean"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","passive","unit"]}')
