VALUES ('B1_U53_B3_I4', 'ingles-b1-U53', 14, 'multiple_choice', 'Choose the correct option to complete each sentence in passive voice.', '{"prompt_es":"Choose the correct option to complete each sentence in passive voice.","mastery_tag":"grammar:b1-passive","explanation":"Para el futuro en pasiva, se utiliza ''will be'' + participio pasado.","stimulus_en":"The project ___ by the team next month.","options":[{"id":"o1","text":"will be finished"},{"id":"o2","text":"is finished"},{"id":"o3","text":"was finished"},{"id":"o4","text":"finishes"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","passive","unit"]}')
