VALUES ('B1_U54_B2_I1', 'ingles-b1-U54', 6, 'multiple_choice', 'Choose the correct answer to complete the sentences about relative clauses.', '{"prompt_es":"Choose the correct answer to complete the sentences about relative clauses.","mastery_tag":"grammar:b1-relative-clauses","explanation":"En esta oración, ''that'' se usa para definir el libro específico que se presta.","stimulus_en":"The book ___ you lent me was fascinating.","options":[{"id":"o1","text":"who"},{"id":"o2","text":"which"},{"id":"o3","text":"that"},{"id":"o4","text":"where"}],"correct_answer":"o3","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
