VALUES ('B1_U54_B2_I2', 'ingles-b1-U54', 7, 'multiple_choice', 'Choose the correct answer to complete the sentences about relative clauses.', '{"prompt_es":"Choose the correct answer to complete the sentences about relative clauses.","mastery_tag":"grammar:b1-relative-clauses","explanation":"Se usa ''who'' para referirse a personas, en este caso un gerente.","stimulus_en":"I met a manager ___ works in marketing.","options":[{"id":"o1","text":"who"},{"id":"o2","text":"that"},{"id":"o3","text":"which"},{"id":"o4","text":"when"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
