VALUES ('B1_U54_B2_I4', 'ingles-b1-U54', 9, 'multiple_choice', 'Choose the correct answer to complete the sentences about relative clauses.', '{"prompt_es":"Choose the correct answer to complete the sentences about relative clauses.","mastery_tag":"grammar:b1-relative-clauses","explanation":"''That'' se usa para referirse específicamente al proyecto en cuestión.","stimulus_en":"This is the project ___ I worked last year.","options":[{"id":"o1","text":"where"},{"id":"o2","text":"that"},{"id":"o3","text":"which"},{"id":"o4","text":"when"}],"correct_answer":"o2","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
