VALUES ('B1_U54_B2_I5', 'ingles-b1-U54', 10, 'multiple_choice', 'Choose the correct answer to complete the sentences about relative clauses.', '{"prompt_es":"Choose the correct answer to complete the sentences about relative clauses.","mastery_tag":"grammar:b1-relative-clauses","explanation":"''When'' se usa para referirse a un momento o evento específico.","stimulus_en":"I remember the day ___ we first met.","options":[{"id":"o1","text":"who"},{"id":"o2","text":"when"},{"id":"o3","text":"that"},{"id":"o4","text":"where"}],"correct_answer":"o2","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
