INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U56_B1_I1', 'ingles-b1-U56', 1, 'fill_blanks', 'Complete the sentences with the correct form of the verb in present perfect continuous.', '{"prompt_es":"Complete the sentences with the correct form of the verb in present perfect continuous.","mastery_tag":"grammar:b1-present-perfect-continuous","explanation":"La estructura del presente perfecto continuo se utiliza para expresar una acción que comenzó en el pasado y continúa hasta el presente. En este caso, ''has been studying'' indica que la acción de estudiar ha estado sucediendo durante un período de tiempo.","stimulus_en":"Fill in the blanks with the correct verb forms.\n\nShe ___ (study) for three hours for her exam.","correct_answer":"has been studying","complexity":2,"concept_tags":["grammar","present","perfect","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U56_B1_I2', 'ingles-b1-U56', 2, 'fill_blanks', 'Complete the sentences with the correct form of the verb in present perfect continuous.', '{"prompt_es":"Complete the sentences with the correct form of the verb in present perfect continuous.","mastery_tag":"grammar:b1-present-perfect-continuous","explanation":"El presente perfecto continuo se usa aquí para mostrar que ''han estado esperando'' el autobús desde una hora específica en el pasado hasta ahora.","stimulus_en":"Fill in the blanks with the correct verb forms.\n\nThey ___ (wait) for the bus since 8 a.m.","correct_answer":"have been waiting","complexity":2,"concept_tags":["grammar","present","perfect","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U56_B1_I3', 'ingles-b1-U56', 3, 'fill_blanks', 'Complete the sentences with the correct form of the verb in present perfect continuous.', '{"prompt_es":"Complete the sentences with the correct form of the verb in present perfect continuous.","mastery_tag":"grammar:b1-present-perfect-continuous","explanation":"En este caso, el uso del presente perfecto continuo indica que la acción de hacer ejercicio ha estado ocurriendo regularmente durante los últimos meses.","stimulus_en":"Fill in the blanks with the correct verb forms.\n\nI ___ (exercise) regularly for the past few months.","correct_answer":"have been exercising","complexity":2,"concept_tags":["grammar","present","perfect","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U56_B1_I4', 'ingles-b1-U56', 4, 'fill_blanks', 'Complete the sentences with the correct form of the verb in present perfect continuous.', '{"prompt_es":"Complete the sentences with the correct form of the verb in present perfect continuous.","mastery_tag":"grammar:b1-present-perfect-continuous","explanation":"El presente perfecto continuo se usa para mostrar que la acción de trabajar en el proyecto ha estado sucediendo durante un período específico de tiempo.","stimulus_en":"Fill in the blanks with the correct verb forms.\n\nWe ___ (work) on this project for two weeks now.","correct_answer":"have been working","complexity":2,"concept_tags":["grammar","present","perfect","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U56_B1_I5', 'ingles-b1-U56', 5, 'fill_blanks', 'Complete the sentences with the correct form of the verb in present perfect continuous.', '{"prompt_es":"Complete the sentences with the correct form of the verb in present perfect continuous.","mastery_tag":"grammar:b1-present-perfect-continuous","explanation":"Aquí, el presente perfecto continuo se emplea para enfatizar que él ha estado aprendiendo español desde un punto específico en el pasado.","stimulus_en":"Fill in the blanks with the correct verb forms.\n\nHe ___ (learn) Spanish since last year.","correct_answer":"has been learning","complexity":2,"concept_tags":["grammar","present","perfect","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U56_B2_I1', 'ingles-b1-U56', 6, 'multiple_choice_cloze', 'Fill in the gaps with the correct option from the choices given.', '{"prompt_es":"Fill in the gaps with the correct option from the choices given.","mastery_tag":"vocabulary:b1-technology","main_text":"In today''s world, many people use [GAP 1] (1)[GAP 2] to communicate and share information. Social media platforms make it easy to connect with friends and [GAP 3] (2)[GAP 4] about our lives. However, it is also important to be aware of [GAP 5] (3)[GAP 6] security when using these platforms. Many users do not realize that their personal [GAP 7] (4)[GAP 8] can be at risk. To protect yourself, always use strong [GAP 9] (5)[GAP 10] and be careful about what you share online.","gaps":[{"id":"1","options":[{"id":"o1","text":"computers"},{"id":"o2","text":"television"},{"id":"o3","text":"phones"},{"id":"o4","text":"tablets"}]},{"id":"2","options":[{"id":"o1","text":"talk"},{"id":"o2","text":"scream"},{"id":"o3","text":"walk"},{"id":"o4","text":"sleep"}]},{"id":"3","options":[{"id":"o1","text":"internet"},{"id":"o2","text":"media"},{"id":"o3","text":"data"},{"id":"o4","text":"computer"}]},{"id":"4","options":[{"id":"o1","text":"information"},{"id":"o2","text":"game"},{"id":"o3","text":"money"},{"id":"o4","text":"time"}]},{"id":"5","options":[{"id":"o1","text":"codes"},{"id":"o2","text":"passwords"},{"id":"o3","text":"files"},{"id":"o4","text":"documents"}]}],"correct_answer":{"1":"o3","2":"o1","3":"o3","4":"o1","5":"o2"},"explanation":"(1) En este contexto, los teléfonos se utilizan para comunicarse y compartir información.\n(2) El verbo ''hablar'' es el apropiado para referirse a la acción de comunicarse en redes sociales.\n(3) La seguridad de los ''datos'' es un tema importante al tratar con plataformas de redes sociales.\n(4) La ''información'' personal es lo que puede estar en riesgo al usar internet.\n(5) Las ''contraseñas'' son cruciales para protegerse en línea.","complexity":2,"concept_tags":["vocabulary","technology","unit","grammar"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U56_B3_I1', 'ingles-b1-U56', 7, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''was'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''was'':","mastery_tag":"grammar:b1-past-continuous","explanation":"En esta frase, se utiliza el pasado continuo para indicar que la acción de trabajar estaba en proceso cuando otra acción (llegar) ocurrió.","stimulus_en":"The team worked on the project when I arrived. (was)\n\nThe team was working on the project ...","correct_answer":"when I arrived.","example":"was","complexity":2,"concept_tags":["grammar","past","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U56_B3_I2', 'ingles-b1-U56', 8, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''was'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''was'':","mastery_tag":"grammar:b1-past-continuous","explanation":"Se usa el pasado continuo para mostrar que dos acciones estaban ocurriendo simultáneamente.","stimulus_en":"She read the report while I was preparing the presentation. (was)\n\nShe was reading the report ...","correct_answer":"while I was preparing the presentation.","example":"was","complexity":2,"concept_tags":["grammar","past","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U56_B3_I3', 'ingles-b1-U56', 9, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''were'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''were'':","mastery_tag":"grammar:b1-past-continuous","explanation":"El pasado continuo se utiliza para enfatizar que la discusión estaba en curso durante la llamada.","stimulus_en":"They were discussing the budget when I called. (were)\n\nThey were having a discussion about the budget ...","correct_answer":"when I called.","example":"were","complexity":2,"concept_tags":["grammar","past","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U56_B3_I4', 'ingles-b1-U56', 10, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''was'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''was'':","mastery_tag":"grammar:b1-past-continuous","explanation":"Aquí, el pasado continuo muestra que la acción de escribir estaba en progreso cuando ocurrió el fallo.","stimulus_en":"I was typing the email when the computer crashed. (was)\n\nI was writing the email ...","correct_answer":"when the computer crashed.","example":"was","complexity":2,"concept_tags":["grammar","past","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U56_B3_I5', 'ingles-b1-U56', 11, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''were'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''were'':","mastery_tag":"grammar:b1-past-continuous","explanation":"Se usa el pasado continuo para mostrar que la reunión estaba en curso cuando llegó el CEO.","stimulus_en":"While we were holding the meeting, the CEO arrived. (were)\n\nWhile we were having the meeting ...","correct_answer":"the CEO arrived.","example":"were","complexity":2,"concept_tags":["grammar","past","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;

