INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U57_B1_I1', 'ingles-b1-U57', 1, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''for'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''for'':","mastery_tag":"grammar:b1-present-perfect-continuous","explanation":"El presente perfecto continuo se utiliza para hablar de acciones que comenzaron en el pasado y continúan en el presente, usando ''for'' para indicar la duración.","stimulus_en":"She started studying English three years ago.\n\nShe has been studying English... ...","correct_answer":"for three years.","example":"for","complexity":2,"concept_tags":["grammar","present","perfect","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U57_B1_I2', 'ingles-b1-U57', 2, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''since'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''since'':","mastery_tag":"grammar:b1-present-perfect-continuous","explanation":"Usamos ''since'' para indicar el punto específico en el tiempo donde comenzó la acción que sigue en el presente.","stimulus_en":"They began playing football last summer.\n\nThey have been playing football... ...","correct_answer":"since last summer.","example":"since","complexity":2,"concept_tags":["grammar","present","perfect","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U57_B1_I3', 'ingles-b1-U57', 3, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''for'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''for'':","mastery_tag":"grammar:b1-present-perfect-continuous","explanation":"El ''for'' se utiliza para expresar la duración de la acción que comenzó en el pasado y sigue en el presente.","stimulus_en":"I started working here two months ago.\n\nI have been working here... ...","correct_answer":"for two months.","example":"for","complexity":2,"concept_tags":["grammar","present","perfect","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U57_B1_I4', 'ingles-b1-U57', 4, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''since'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''since'':","mastery_tag":"grammar:b1-present-perfect-continuous","explanation":"Usamos ''since'' para indicar cuando comenzó la acción que continúa hasta ahora.","stimulus_en":"He has been running every day. This started in January.\n\nHe has been running every day... ...","correct_answer":"since January.","example":"since","complexity":2,"concept_tags":["grammar","present","perfect","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U57_B1_I5', 'ingles-b1-U57', 5, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''for'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''for'':","mastery_tag":"grammar:b1-present-perfect-continuous","explanation":"El ''for'' muestra la duración de la actividad que comenzó en el pasado y sigue en el presente.","stimulus_en":"They started planning the event last month.\n\nThey have been planning the event... ...","correct_answer":"for a month.","example":"for","complexity":2,"concept_tags":["grammar","present","perfect","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U57_B2_I1', 'ingles-b1-U57', 6, 'multiple_choice', 'Choose the correct option to complete the sentences using reported speech.', '{"prompt_es":"Choose the correct option to complete the sentences using reported speech.","mastery_tag":"grammar:b1-reported-speech","explanation":"En el discurso indirecto, usamos el pasado para reportar algo que se dijo en presente.","stimulus_en":"She said, ''I am working on a new project.'' --> She said that she ___ on a new project.","options":[{"id":"o1","text":"is working"},{"id":"o2","text":"was working"},{"id":"o3","text":"works"},{"id":"o4","text":"worked"}],"correct_answer":"o2","complexity":2,"concept_tags":["grammar","reported","speech","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U57_B2_I2', 'ingles-b1-U57', 7, 'multiple_choice', 'He asked, ''Are you attending the conference?'' --> He asked if I ___ attending the conference.', '{"prompt_es":"He asked, ''Are you attending the conference?'' --> He asked if I ___ attending the conference.","mastery_tag":"grammar:b1-reported-speech","explanation":"Cuando se hace una pregunta en directo, en el discurso indirecto cambiamos el tiempo verbal al pasado.","stimulus_en":"","options":[{"id":"o1","text":"was"},{"id":"o2","text":"am"},{"id":"o3","text":"is"},{"id":"o4","text":"will be"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","reported","speech","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U57_B2_I3', 'ingles-b1-U57', 8, 'multiple_choice', 'Choose the correct option to complete the sentences using reported speech.', '{"prompt_es":"Choose the correct option to complete the sentences using reported speech.","mastery_tag":"grammar:b1-reported-speech","explanation":"El futuro se transforma en ''would'' cuando lo reportamos.","stimulus_en":"They told me, ''We will finish the report tomorrow.'' --> They told me that they ___ the report the next day.","options":[{"id":"o1","text":"will finish"},{"id":"o2","text":"finish"},{"id":"o3","text":"would finish"},{"id":"o4","text":"finished"}],"correct_answer":"o3","complexity":2,"concept_tags":["grammar","reported","speech","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U57_B2_I4', 'ingles-b1-U57', 9, 'multiple_choice', 'Choose the correct option to complete the sentences using reported speech.', '{"prompt_es":"Choose the correct option to complete the sentences using reported speech.","mastery_tag":"grammar:b1-reported-speech","explanation":"Para los modales, cambiamos ''can'' a ''could'' en el discurso reportado.","stimulus_en":"James said, ''I can''t attend the meeting.'' --> James said that he ___ attend the meeting.","options":[{"id":"o1","text":"can"},{"id":"o2","text":"could"},{"id":"o3","text":"can''t"},{"id":"o4","text":"couldn''t"}],"correct_answer":"o4","complexity":2,"concept_tags":["grammar","reported","speech","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U57_B2_I5', 'ingles-b1-U57', 10, 'multiple_choice', 'Choose the correct option to complete the sentences using reported speech.', '{"prompt_es":"Choose the correct option to complete the sentences using reported speech.","mastery_tag":"grammar:b1-reported-speech","explanation":"El futuro se convierte en ''would'' en el discurso indirecto.","stimulus_en":"She said, ''I will send you the documents.'' --> She said that she ___ me the documents soon.","options":[{"id":"o1","text":"will send"},{"id":"o2","text":"would send"},{"id":"o3","text":"sends"},{"id":"o4","text":"sent"}],"correct_answer":"o2","complexity":2,"concept_tags":["grammar","reported","speech","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U57_B3_I1', 'ingles-b1-U57', 11, 'fill_blanks', 'Fill in the blanks with the most appropriate word.', '{"prompt_es":"Fill in the blanks with the most appropriate word.","mastery_tag":"reading:b1-articles-reviews","main_text":"In recent years, many people have started to rely on online reviews before making a purchase. Some reviews are very detailed, while others are quite short. It is important to read (1)___ reviews from different sources to form (2)___ opinion. Many websites allow users to rate products and share their experiences. However, not all reviews are trustworthy, so it is wise to be (3)___ when reading them. When writing a review, one should include (4)___ information, such as the benefits and drawbacks of the product. This helps other consumers make (5)___ informed choices.","correct_answer":["multiple","their","cautious","specific","more"],"explanation":"(1) En este contexto, se refiere a leer ''varias'' reseñas para obtener una mejor perspectiva.\n(2) Se refiere a tener ''su'' propia opinión basada en diferentes reseñas.\n(3) Significa ser ''cauteloso'' al leer reseñas para evitar información engañosa.\n(4) Aquí se sugiere incluir ''información específica'' que ayude a otros consumidores.\n(5) Indica que las decisiones se vuelven ''más'' informadas con buena información.","complexity":2,"concept_tags":["reading","articles","reviews","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;

