VALUES ('B1_U58_B1_I1', 'ingles-b1-U58', 1, 'multiple_choice', 'What is the main purpose of a film festival?', '{"prompt_es":"What is the main purpose of a film festival?","mastery_tag":"vocabulary:b1-entertainment","explanation":"Un festival de cine se organiza principalmente para exhibir y celebrar películas, no para vender productos o enseñar.","stimulus_en":"","options":[{"id":"o1","text":"To sell products"},{"id":"o2","text":"To showcase films"},{"id":"o3","text":"To teach students"},{"id":"o4","text":"To make money"}],"correct_answer":"o2","complexity":2,"concept_tags":["vocabulary","entertainment","unit","grammar"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U58_B1_I2', 'ingles-b1-U58', 2, 'multiple_choice', 'Which genre of music typically features heavy use of guitars and drums?', '{"prompt_es":"Which genre of music typically features heavy use of guitars and drums?","mastery_tag":"vocabulary:b1-entertainment","explanation":"El rock es conocido por su fuerte uso de guitarras eléctricas y batería, mientras que otros géneros como la música clásica y el jazz tienen diferentes instrumentos predominantes.","stimulus_en":"","options":[{"id":"o1","text":"Classical"},{"id":"o2","text":"Jazz"},{"id":"o3","text":"Rock"},{"id":"o4","text":"Opera"}],"correct_answer":"o3","complexity":2,"concept_tags":["vocabulary","entertainment","unit","grammar"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U58_B1_I3', 'ingles-b1-U58', 3, 'multiple_choice', 'What type of art involves creating images using colored materials on a surface?', '{"prompt_es":"What type of art involves creating images using colored materials on a surface?","mastery_tag":"vocabulary:b1-entertainment","explanation":"La pintura se refiere a la técnica de crear imágenes en una superficie, mientras que la escultura implica modelar materiales en tres dimensiones.","stimulus_en":"","options":[{"id":"o1","text":"Sculpture"},{"id":"o2","text":"Painting"},{"id":"o3","text":"Photography"},{"id":"o4","text":"Installation"}],"correct_answer":"o2","complexity":2,"concept_tags":["vocabulary","entertainment","unit","grammar"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U58_B1_I4', 'ingles-b1-U58', 4, 'multiple_choice', 'Which of the following is a famous musical theater production?', '{"prompt_es":"Which of the following is a famous musical theater production?","mastery_tag":"vocabulary:b1-entertainment","explanation":"Les Misérables es una producción de teatro musical muy conocida, mientras que las otras opciones son novelas.","stimulus_en":"","options":[{"id":"o1","text":"The Great Gatsby"},{"id":"o2","text":"Les Misérables"},{"id":"o3","text":"Pride and Prejudice"},{"id":"o4","text":"The Catcher in the Rye"}],"correct_answer":"o2","complexity":2,"concept_tags":["vocabulary","entertainment","unit","grammar"]}')
