VALUES ('B1_U59_B3_I2', 'ingles-b1-U59', 4, 'multiple_choice', 'What is the term for a performance art that involves singing, dancing, and acting?', '{"prompt_es":"What is the term for a performance art that involves singing, dancing, and acting?","mastery_tag":"vocabulary:b1-entertainment","explanation":"Un musical es una forma de arte escénico que combina canto, baile y actuación.","stimulus_en":"","options":[{"id":"o1","text":"Musical"},{"id":"o2","text":"Opera"},{"id":"o3","text":"Concert"},{"id":"o4","text":"Play"}],"correct_answer":"o1","complexity":2,"concept_tags":["vocabulary","entertainment","unit","grammar"]}')
