INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U6_B2_I5', 'ingles-b1-U6', 6, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''send'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''send'':","mastery_tag":"grammar:b1-passive","explanation":"Convertir a voz activa permite ver claramente quién realiza la acción (la secretaria).","stimulus_en":"The documents will be sent by the secretary.\n\nThe secretary will ...","correct_answer":"send the documents.","example":"send","complexity":2,"concept_tags":["grammar","passive","unit"]}')
