INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U8_B1_I2', 'ingles-b1-U8', 2, 'multiple_choice', 'Choose the correct option to complete each sentence about relative clauses.', '{"prompt_es":"Choose the correct option to complete each sentence about relative clauses.","mastery_tag":"grammar:b1-relative-clauses","explanation":"Aquí, ''which'' se usa porque nos referimos a un informe, que es un objeto.","stimulus_en":"The report, ___ was submitted yesterday, is very detailed.","options":[{"id":"o1","text":"which"},{"id":"o2","text":"who"},{"id":"o3","text":"that"},{"id":"o4","text":"where"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
