INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U8_B1_I3', 'ingles-b1-U8', 3, 'multiple_choice', 'Choose the correct option to complete each sentence about relative clauses.', '{"prompt_es":"Choose the correct option to complete each sentence about relative clauses.","mastery_tag":"grammar:b1-relative-clauses","explanation":"''Where'' se usa para referirse a un lugar, en este caso, el lugar de la reunión.","stimulus_en":"This is the place ___ we met last year.","options":[{"id":"o1","text":"when"},{"id":"o2","text":"where"},{"id":"o3","text":"who"},{"id":"o4","text":"that"}],"correct_answer":"o2","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
