INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U8_B1_I4', 'ingles-b1-U8', 4, 'multiple_choice', 'Choose the correct option to complete each sentence about relative clauses.', '{"prompt_es":"Choose the correct option to complete each sentence about relative clauses.","mastery_tag":"grammar:b1-relative-clauses","explanation":"''That'' se puede usar para referirse a objetos, como libros, en oraciones de relativa.","stimulus_en":"I read a book ___ was written by a famous author.","options":[{"id":"o1","text":"who"},{"id":"o2","text":"that"},{"id":"o3","text":"when"},{"id":"o4","text":"where"}],"correct_answer":"o2","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
