INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U8_B1_I5', 'ingles-b1-U8', 5, 'multiple_choice', 'Choose the correct option to complete each sentence about relative clauses.', '{"prompt_es":"Choose the correct option to complete each sentence about relative clauses.","mastery_tag":"grammar:b1-relative-clauses","explanation":"''Which'' es la opción correcta porque se refiere a la reunión, que es un objeto.","stimulus_en":"The meeting, ___ was on Friday, was very informative.","options":[{"id":"o1","text":"which"},{"id":"o2","text":"who"},{"id":"o3","text":"when"},{"id":"o4","text":"that"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
