INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U8_B2_I1', 'ingles-b1-U8', 6, 'multiple_choice', 'What is the main purpose of a job interview?', '{"prompt_es":"What is the main purpose of a job interview?","mastery_tag":"listening:b1-interviews","explanation":"El propósito principal de una entrevista de trabajo es evaluar a los candidatos para determinar si son adecuados para el puesto.","stimulus_en":"","options":[{"id":"o1","text":"To socialize"},{"id":"o2","text":"To evaluate candidates"},{"id":"o3","text":"To discuss hobbies"},{"id":"o4","text":"To meet friends"}],"correct_answer":"o2","complexity":2,"concept_tags":["listening","interviews","unit","grammar"]}')
