INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U8_B2_I4', 'ingles-b1-U8', 9, 'multiple_choice', 'Why is it good to prepare for an interview?', '{"prompt_es":"Why is it good to prepare for an interview?","mastery_tag":"listening:b1-interviews","explanation":"Prepararse para una entrevista ayuda a sentirte seguro y a responder mejor las preguntas.","stimulus_en":"","options":[{"id":"o1","text":"To look busy"},{"id":"o2","text":"To relax"},{"id":"o3","text":"To feel confident"},{"id":"o4","text":"To avoid questions"}],"correct_answer":"o3","complexity":2,"concept_tags":["listening","interviews","unit","grammar"]}')
