INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U8_B3_I1', 'ingles-b1-U8', 11, 'multiple_choice', 'What is the main function of a CPU in a computer?', '{"prompt_es":"What is the main function of a CPU in a computer?","mastery_tag":"vocabulary:b1-technology","explanation":"El CPU (Unidad Central de Procesamiento) es el cerebro del ordenador, responsable de procesar la información y ejecutar instrucciones.","stimulus_en":"","options":[{"id":"o1","text":"To store data"},{"id":"o2","text":"To process information"},{"id":"o3","text":"To display graphics"},{"id":"o4","text":"To connect to the internet"}],"correct_answer":"o2","complexity":2,"concept_tags":["vocabulary","technology","unit","grammar"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
