VALUES ('B1_U8_B3_I2', 'ingles-b1-U8', 12, 'multiple_choice', 'Which of the following is a popular social media platform?', '{"prompt_es":"Which of the following is a popular social media platform?","mastery_tag":"vocabulary:b1-technology","explanation":"Instagram es una plataforma de redes sociales donde los usuarios pueden compartir fotos y vídeos.","stimulus_en":"","options":[{"id":"o1","text":"Microsoft Word"},{"id":"o2","text":"Instagram"},{"id":"o3","text":"Adobe Photoshop"},{"id":"o4","text":"JavaScript"}],"correct_answer":"o2","complexity":2,"concept_tags":["vocabulary","technology","unit","grammar"]}')
