INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U11_B1_I1', 'ingles-b1-U11', 1, 'fill_blanks', 'Fill in the blanks with the correct words to complete the sentences about interview conversations.', '{"prompt_es":"Fill in the blanks with the correct words to complete the sentences about interview conversations.","mastery_tag":"listening:b1-interviews","main_text":"During the interview, the candidate showed great ___ in her abilities.","correct_answer":["confidence","experience","answers","qualifications","steps"],"explanation":"(1) La confianza es la creencia en las propias habilidades, y es importante demostrarla en una entrevista.\n(2) La experiencia se refiere a la práctica y conocimiento que una persona tiene en un área específica.\n(3) Las respuestas preparadas ayudan a sentirse más seguro y a dar una buena impresión.\n(4) Las cualificaciones son las habilidades y la educación que hacen a un candidato adecuado para un trabajo.\n(5) Preguntar sobre los próximos pasos muestra interés y ayuda a entender el proceso de contratación.","complexity":2,"concept_tags":["listening","interviews","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U11_B2_I1', 'ingles-b1-U11', 2, 'multiple_choice', 'Choose the correct option to complete the sentences about reported speech.', '{"prompt_es":"Choose the correct option to complete the sentences about reported speech.","mastery_tag":"grammar:b1-reported-speech","explanation":"En el estilo indirecto, el tiempo verbal se retrocede. ''Am going'' cambia a ''was going''.","stimulus_en":"She said, ''I am going to the market.'' → She said that she ___ to the market.","options":[{"id":"o1","text":"is going"},{"id":"o2","text":"was going"},{"id":"o3","text":"go"},{"id":"o4","text":"went"}],"correct_answer":"o2","complexity":2,"concept_tags":["grammar","reported","speech","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U11_B2_I2', 'ingles-b1-U11', 3, 'multiple_choice', 'He asked, ''Do you like coffee?'' → He asked if I ___ coffee.', '{"prompt_es":"He asked, ''Do you like coffee?'' → He asked if I ___ coffee.","mastery_tag":"grammar:b1-reported-speech","explanation":"Al reportar preguntas en estilo indirecto, el verbo también cambia de tiempo. ''Do you like'' se convierte en ''liked''.","stimulus_en":"","options":[{"id":"o1","text":"like"},{"id":"o2","text":"liked"},{"id":"o3","text":"am liking"},{"id":"o4","text":"will like"}],"correct_answer":"o2","complexity":2,"concept_tags":["grammar","reported","speech","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U11_B2_I3', 'ingles-b1-U11', 4, 'multiple_choice', 'Choose the correct option to complete the sentences about reported speech.', '{"prompt_es":"Choose the correct option to complete the sentences about reported speech.","mastery_tag":"grammar:b1-reported-speech","explanation":"El futuro simple ''will finish'' cambia a ''would finish'' en estilo indirecto.","stimulus_en":"They said, ''We will finish the project tomorrow.'' → They said that they ___ the project the next day.","options":[{"id":"o1","text":"will finish"},{"id":"o2","text":"finished"},{"id":"o3","text":"would finish"},{"id":"o4","text":"are finishing"}],"correct_answer":"o3","complexity":2,"concept_tags":["grammar","reported","speech","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U11_B2_I4', 'ingles-b1-U11', 5, 'multiple_choice', 'Choose the correct option to complete the sentences about reported speech.', '{"prompt_es":"Choose the correct option to complete the sentences about reported speech.","mastery_tag":"grammar:b1-reported-speech","explanation":"El presente perfecto ''have seen'' cambia a ''had seen'' en el discurso reportado.","stimulus_en":"Maria told me, ''I have seen that movie.'' → Maria told me that she ___ that movie.","options":[{"id":"o1","text":"has seen"},{"id":"o2","text":"had seen"},{"id":"o3","text":"saw"},{"id":"o4","text":"is seeing"}],"correct_answer":"o2","complexity":2,"concept_tags":["grammar","reported","speech","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U11_B2_I5', 'ingles-b1-U11', 6, 'multiple_choice', 'John asked, ''Where are you going?'' → John asked where I ___ going.', '{"prompt_es":"John asked, ''Where are you going?'' → John asked where I ___ going.","mastery_tag":"grammar:b1-reported-speech","explanation":"En el discurso indirecto, el tiempo presente ''are'' cambia a pasado ''was''.","stimulus_en":"","options":[{"id":"o1","text":"was"},{"id":"o2","text":"am"},{"id":"o3","text":"will be"},{"id":"o4","text":"is"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","reported","speech","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U11_B3_I1', 'ingles-b1-U11', 7, 'multiple_choice_cloze', 'Fill in the blanks with the correct word from the options provided.', '{"prompt_es":"Fill in the blanks with the correct word from the options provided.","mastery_tag":"vocabulary:b1-technology","main_text":"In today''s world, (1)[GAP 1] is a vital part of our daily lives. We use the internet for (2)[GAP 2], shopping, and connecting with friends. Social media platforms allow us to share (3)[GAP 3] and communicate quickly. However, we must also be careful of (4)[GAP 4], as not all information online is accurate. Understanding basic (5)[GAP 5] skills is essential for everyone.","gaps":[{"id":"1","options":[{"id":"o1","text":"technology"},{"id":"o2","text":"biology"},{"id":"o3","text":"history"},{"id":"o4","text":"literature"}]},{"id":"2","options":[{"id":"o1","text":"learning"},{"id":"o2","text":"running"},{"id":"o3","text":"eating"},{"id":"o4","text":"sleeping"}]},{"id":"3","options":[{"id":"o1","text":"photos"},{"id":"o2","text":"music"},{"id":"o3","text":"clouds"},{"id":"o4","text":"books"}]},{"id":"4","options":[{"id":"o1","text":"advertising"},{"id":"o2","text":"privacy"},{"id":"o3","text":"danger"},{"id":"o4","text":"technology"}]},{"id":"5","options":[{"id":"o1","text":"computer"},{"id":"o2","text":"cooking"},{"id":"o3","text":"negotiation"},{"id":"o4","text":"life"}]}],"correct_answer":{"1":"o1","2":"o1","3":"o1","4":"o2","5":"o1"},"explanation":"(1) La tecnología es una parte vital de nuestras vidas hoy en día, así que la opción correcta es ''technology''.\n(2) Usamos el internet principalmente para aprender, así que la respuesta adecuada es ''learning''.\n(3) Las plataformas de redes sociales permiten compartir fotos, así que la opción correcta es ''photos''.\n(4) Debemos tener cuidado con la privacidad en línea, así que la respuesta correcta es ''privacy''.\n(5) Las habilidades informáticas son esenciales, así que la opción correcta es ''computer''.","complexity":2,"concept_tags":["vocabulary","technology","unit","grammar"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;

INSERT INTO public.course_lessons (id, module_id, order_index, title, duration)
VALUES ('ingles-b1-U12', 'ingles-b1', 12, 'Unit 12', 600)
ON CONFLICT (id) DO UPDATE SET title = EXCLUDED.title, order_index = EXCLUDED.order_index;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U12_B1_I1', 'ingles-b1-U12', 1, 'fill_blanks', 'Fill in the blanks with the correct words.', '{"prompt_es":"Fill in the blanks with the correct words.","mastery_tag":"reading:b1-articles-reviews","main_text":"In recent years, many people have started to pay more attention to (1)___ (food) choices. With the rise of social media, sharing (2)___ (review) about restaurants and cafes has become very popular. People want to know what others think before trying (3)___ (new) places. Some reviews are very (4)___ (help) and provide useful information, while others may be (5)___ (rely) and just personal opinions.","correct_answer":["food","reviews","new","helpful","unreliable"],"explanation":"(1) En este contexto, se espera una palabra que se relacione con ''comida'' o ''opciones alimenticias''.\n(2) La palabra correcta es el plural de ''reseña'', ya que se habla de múltiples opiniones.\n(3) La palabra que se necesita aquí es un adjetivo que describe ''lugares'', indicando que son recientes o no visitados antes.\n(4) Buscamos un adjetivo que describa revisiones que son útiles para los lectores.\n(5) En este caso, se necesita un adjetivo que describa opiniones que no son confiables o que pueden no ser verdaderas.","complexity":2,"concept_tags":["reading","articles","reviews","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U12_B2_I1', 'ingles-b1-U12', 2, 'fill_blanks', 'Complete the sentences with the correct form of the verbs in brackets.', '{"prompt_es":"Complete the sentences with the correct form of the verbs in brackets.","mastery_tag":"grammar:b1-conditionals","main_text":"If it rains tomorrow, we ___ (stay) at home.\n\nIf I had a million dollars, I ___ (travel) the world.\n\nIf she studies hard, she ___ (pass) the exam.\n\nIf I were you, I ___ (take) that job.\n\nIf they had known about the party, they ___ (come).","correct_answer":["will stay","would travel","will pass","would take","would have come"],"explanation":"(1) En esta oración, estamos hablando de una situación real que puede suceder en el futuro. Usamos ''will'' con el verbo en su forma base.\n(2) Aquí estamos hablando de una situación hipotética en el presente o futuro. Usamos ''would'' para referirnos a acciones que no son reales.\n(3) Esta es una condición real donde el esfuerzo puede llevar a un resultado positivo. Usamos ''will'' para indicar la consecuencia.\n(4) En esta oración, usamos ''were'' en lugar de ''was'' para situaciones hipotéticas que son irreales. Se usa ''would'' para expresar un consejo o recomendación.\n(5) Esta oración se refiere a una situación en el pasado que no ocurrió. Usamos ''would have'' más el participio pasado del verbo para expresar esto.","complexity":2,"concept_tags":["grammar","conditionals","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U12_B3_I1', 'ingles-b1-U12', 3, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''for'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''for'':","mastery_tag":"grammar:b1-present-perfect-continuous","explanation":"En esta oración se está hablando de una acción que comenzó en el pasado y continúa en el presente, por lo que se utiliza el presente perfecto continuo.","stimulus_en":"She started studying for the exam two hours ago. (for)\n\nShe has been studying... ...","correct_answer":"for two hours.","example":"for","complexity":2,"concept_tags":["grammar","present","perfect","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U12_B3_I2', 'ingles-b1-U12', 4, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''since'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''since'':","mastery_tag":"grammar:b1-present-perfect-continuous","explanation":"Se usa ''since'' para señalar el inicio de la acción en un momento específico en el pasado.","stimulus_en":"They are playing football since 3 PM. (since)\n\nThey have been playing... ...","correct_answer":"since 3 PM.","example":"since","complexity":2,"concept_tags":["grammar","present","perfect","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U12_B3_I3', 'ingles-b1-U12', 5, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''for'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''for'':","mastery_tag":"grammar:b1-present-perfect-continuous","explanation":"La acción comenzó en el pasado y sigue en el presente, así que utilizamos el presente perfecto continuo.","stimulus_en":"John works at the company for five years now. (for)\n\nJohn has been working... ...","correct_answer":"for five years.","example":"for","complexity":2,"concept_tags":["grammar","present","perfect","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U12_B3_I4', 'ingles-b1-U12', 6, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''since'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''since'':","mastery_tag":"grammar:b1-present-perfect-continuous","explanation":"El uso de ''since'' nos indica el comienzo de la acción en un momento específico.","stimulus_en":"I am learning English since last year. (since)\n\nI have been learning... ...","correct_answer":"since last year.","example":"since","complexity":2,"concept_tags":["grammar","present","perfect","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U12_B3_I5', 'ingles-b1-U12', 7, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''for'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''for'':","mastery_tag":"grammar:b1-present-perfect-continuous","explanation":"Aquí también hablamos de una acción que comenzó en el pasado y continúa, por eso usamos el presente perfecto continuo.","stimulus_en":"We started exercising regularly for three months now. (for)\n\nWe have been exercising... ...","correct_answer":"for three months.","example":"for","complexity":2,"concept_tags":["grammar","present","perfect","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;

