VALUES ('B1_U13_B1_I4', 'ingles-b1-U13', 4, 'multiple_choice', 'Which of the following is NOT a type of film?', '{"prompt_es":"Which of the following is NOT a type of film?","mastery_tag":"vocabulary:b1-entertainment","explanation":"La gastronomía no es un género de película, mientras que los otros son tipos de películas que se producen.","stimulus_en":"","options":[{"id":"o1","text":"Documentary"},{"id":"o2","text":"Animation"},{"id":"o3","text":"Gastronomy"},{"id":"o4","text":"Action"}],"correct_answer":"o3","complexity":2,"concept_tags":["vocabulary","entertainment","unit","grammar"]}')
