INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U13_B2_I1', 'ingles-b1-U13', 6, 'fill_blanks', 'Complete the sentences using the correct conditional form.', '{"prompt_es":"Complete the sentences using the correct conditional form.","mastery_tag":"grammar:b1-conditionals","main_text":"If it ___ (rain), we will stay indoors.\n\nIf I ___ (be) you, I would take that job.\n\nIf she ___ (study) more, she will pass the exam.\n\nIf we ___ (have) time, we would travel more.\n\nIf you ___ (not/try), you will never know.","correct_answer":["rains","were","studies","had","don’t try"],"explanation":"(1) Aquí se usa la primera condicional porque se habla de una situación real en el presente o futuro.\n(2) Aquí se usa la segunda condicional porque se trata de una situación hipotética en el presente.\n(3) En este caso, se usa la primera condicional porque es una posibilidad real.\n(4) Usamos la segunda condicional aquí porque estamos hablando de una situación hipotética.\n(5) Esto es un ejemplo de la primera condicional. Es una advertencia sobre un hecho real.","complexity":2,"concept_tags":["grammar","conditionals","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U13_B3_I1', 'ingles-b1-U13', 7, 'multiple_choice', 'What is the main purpose of an informal letter?', '{"prompt_es":"What is the main purpose of an informal letter?","mastery_tag":"writing:b1-informal-letters","explanation":"El propósito principal de una carta informal es comunicarse de manera casual con amigos o familiares.","stimulus_en":"","options":[{"id":"o1","text":"To complain"},{"id":"o2","text":"To inform formally"},{"id":"o3","text":"To communicate casually"},{"id":"o4","text":"To provide details"}],"correct_answer":"o3","complexity":2,"concept_tags":["writing","informal","letters","unit","grammar"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U13_B3_I2', 'ingles-b1-U13', 8, 'multiple_choice', 'Which phrase is commonly used to start an informal letter?', '{"prompt_es":"Which phrase is commonly used to start an informal letter?","mastery_tag":"writing:b1-informal-letters","explanation":"La frase ''Hi [Name]'' es típica para comenzar cartas informales, ya que es amigable y personal.","stimulus_en":"","options":[{"id":"o1","text":"Dear Sir/Madam"},{"id":"o2","text":"To whom it may concern"},{"id":"o3","text":"Hi [Name]"},{"id":"o4","text":"Greetings"}],"correct_answer":"o3","complexity":2,"concept_tags":["writing","informal","letters","unit","grammar"]}')
