VALUES ('B1_U13_B3_I3', 'ingles-b1-U13', 9, 'multiple_choice', 'Which of these is NOT a common closure for an informal letter?', '{"prompt_es":"Which of these is NOT a common closure for an informal letter?","mastery_tag":"writing:b1-informal-letters","explanation":"La palabra ''Sincerely'' es un cierre formal, no se utiliza en cartas informales.","stimulus_en":"","options":[{"id":"o1","text":"Best wishes"},{"id":"o2","text":"Sincerely"},{"id":"o3","text":"Take care"},{"id":"o4","text":"Love"}],"correct_answer":"o2","complexity":2,"concept_tags":["writing","informal","letters","unit","grammar"]}')
