VALUES ('B1_U14_B1_I1', 'ingles-b1-U14', 1, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''WAS WRITING'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''WAS WRITING'':","mastery_tag":"grammar:b1-past-continuous","explanation":"En esta oración, debes usar el pasado continuo para describir una acción que estaba en progreso cuando ocurrió otra.","stimulus_en":"I (write) an email when you called me.\n\nI was writing... ...","correct_answer":"an email when you called me.","example":"WAS WRITING","complexity":2,"concept_tags":["grammar","past","continuous","unit"]}')
