INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U14_B1_I2', 'ingles-b1-U14', 2, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''WAS DANCING'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''WAS DANCING'':","mastery_tag":"grammar:b1-past-continuous","explanation":"Recuerda que el pasado continuo se usa para mostrar que una acción estaba en progreso en un momento específico en el pasado.","stimulus_en":"She (dance) when the music stopped.\n\nShe was dancing... ...","correct_answer":"when the music stopped.","example":"WAS DANCING","complexity":2,"concept_tags":["grammar","past","continuous","unit"]}')
