INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U14_B1_I3', 'ingles-b1-U14', 3, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''WERE PLAYING'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''WERE PLAYING'':","mastery_tag":"grammar:b1-past-continuous","explanation":"El pasado continuo se utiliza aquí para enfatizar que estaban jugando durante una acción paralela (la lluvia).","stimulus_en":"They (play) soccer while it rained.\n\nThey were playing... ...","correct_answer":"soccer while it rained.","example":"WERE PLAYING","complexity":2,"concept_tags":["grammar","past","continuous","unit"]}')
