INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U14_B2_I2', 'ingles-b1-U14', 7, 'fill_blanks', 'Complete the sentences with the correct form of the past continuous.', '{"prompt_es":"Complete the sentences with the correct form of the past continuous.","mastery_tag":"grammar:b1-past-continuous","explanation":"Aquí, el pasado continuo muestra que la discusión sobre el proyecto estaba ocurriendo al mismo tiempo que la entrada del gerente.","stimulus_en":"During the meeting yesterday, several things happened. Fill in the blanks with the correct past continuous form.\n\nThey ___ (discuss) the project when the manager entered.","correct_answer":"were discussing","complexity":2,"concept_tags":["grammar","past","continuous","unit"]}')
