INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U14_B2_I3', 'ingles-b1-U14', 8, 'fill_blanks', 'Complete the sentences with the correct form of the past continuous.', '{"prompt_es":"Complete the sentences with the correct form of the past continuous.","mastery_tag":"grammar:b1-past-continuous","explanation":"Ambas acciones, escribir un correo electrónico y hacer una presentación, estaban en curso al mismo tiempo.","stimulus_en":"During the meeting yesterday, several things happened. Fill in the blanks with the correct past continuous form.\n\nShe ___ (write) an email while I ___ (make) a presentation.","correct_answer":"was writing, was making","complexity":2,"concept_tags":["grammar","past","continuous","unit"]}')
