INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U16_B1_I1', 'ingles-b1-U16', 1, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''happy'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''happy'':","mastery_tag":"grammar:b1-reported-speech","explanation":"En el estilo indirecto, el tiempo verbal cambia, en este caso de ''am'' a ''was''.","stimulus_en":"She said, ''I am happy.''\n\nShe said that she was... ...","correct_answer":"happy.","example":"happy","complexity":2,"concept_tags":["grammar","reported","speech","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U16_B1_I2', 'ingles-b1-U16', 2, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''tomorrow'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''tomorrow'':","mastery_tag":"grammar:b1-reported-speech","explanation":"La palabra ''tomorrow'' se convierte en ''the next day'' en reported speech.","stimulus_en":"He told me, ''I will finish the report tomorrow.''\n\nHe told me that he would finish the report... ...","correct_answer":"the next day.","example":"tomorrow","complexity":2,"concept_tags":["grammar","reported","speech","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U16_B1_I3', 'ingles-b1-U16', 3, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''meeting'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''meeting'':","mastery_tag":"grammar:b1-reported-speech","explanation":"En reported speech, ''Are you'' cambia a ''was'' y se usa ''if'' para preguntas sí/no.","stimulus_en":"They asked, ''Are you coming to the meeting?''\n\nThey asked if I was coming to the... ...","correct_answer":"meeting.","example":"meeting","complexity":2,"concept_tags":["grammar","reported","speech","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U16_B1_I4', 'ingles-b1-U16', 4, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''application'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''application'':","mastery_tag":"grammar:b1-reported-speech","explanation":"El tiempo del verbo cambia de ''need'' a ''needed'' en el estilo indirecto.","stimulus_en":"She told him, ''You need to submit the application by Friday.''\n\nShe told him that he needed to submit the... ...","correct_answer":"application by Friday.","example":"application","complexity":2,"concept_tags":["grammar","reported","speech","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U16_B1_I5', 'ingles-b1-U16', 5, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''join'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''join'':","mastery_tag":"grammar:b1-reported-speech","explanation":"En reported speech, ''can'' cambia a ''could''.","stimulus_en":"He said, ''I can join the presentation.''\n\nHe said that he could join the... ...","correct_answer":"presentation.","example":"join","complexity":2,"concept_tags":["grammar","reported","speech","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U16_B2_I1', 'ingles-b1-U16', 6, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''stay'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''stay'':","mastery_tag":"grammar:b1-conditionals","explanation":"Esta es una oración en condicional donde usamos ''if'' para mostrar una situación futura.","stimulus_en":"If it rains tomorrow, I will stay at home.\n\nI will stay at home... ...","correct_answer":"if it rains tomorrow.","example":"stay","complexity":2,"concept_tags":["grammar","conditionals","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U16_B2_I2', 'ingles-b1-U16', 7, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''would'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''would'':","mastery_tag":"grammar:b1-conditionals","explanation":"Esta es una oración en condicional que describe una situación hipotética.","stimulus_en":"She doesn’t study for exams, so she fails.\n\nIf she studied for exams, she would... ...","correct_answer":"not fail.","example":"would","complexity":2,"concept_tags":["grammar","conditionals","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U16_B2_I3', 'ingles-b1-U16', 8, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''if'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''if'':","mastery_tag":"grammar:b1-conditionals","explanation":"Aquí usamos el segundo condicional para hablar de un resultado hipotético.","stimulus_en":"He plays football. He is happy.\n\nHe would be happy if... ...","correct_answer":"he played football.","example":"if","complexity":2,"concept_tags":["grammar","conditionals","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U16_B2_I4', 'ingles-b1-U16', 9, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''can'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''can'':","mastery_tag":"grammar:b1-conditionals","explanation":"En este caso, usamos el primer condicional para mostrar una posibilidad real.","stimulus_en":"You finish your homework. You can go out.\n\nIf you finish your homework, you can... ...","correct_answer":"go out.","example":"can","complexity":2,"concept_tags":["grammar","conditionals","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U16_B2_I5', 'ingles-b1-U16', 10, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''if'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''if'':","mastery_tag":"grammar:b1-conditionals","explanation":"Esta oración muestra un deseo irreal que se relaciona con el segundo condicional.","stimulus_en":"I would travel to Spain. I had more time.\n\nI would travel to Spain if... ...","correct_answer":"I had more time.","example":"if","complexity":2,"concept_tags":["grammar","conditionals","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U16_B3_I1', 'ingles-b1-U16', 11, 'multiple_choice', 'Choose the correct passive form to complete each sentence.', '{"prompt_es":"Choose the correct passive form to complete each sentence.","mastery_tag":"grammar:b1-passive","explanation":"En la voz pasiva, se utiliza ''is'' con el participio pasado ''written'' para expresar que la acción fue realizada por alguien.","stimulus_en":"The book ___ by J.K. Rowling.","options":[{"id":"o1","text":"is written"},{"id":"o2","text":"was wrote"},{"id":"o3","text":"writes"},{"id":"o4","text":"wrote"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","passive","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U16_B3_I2', 'ingles-b1-U16', 12, 'multiple_choice', 'Choose the correct passive form to complete each sentence.', '{"prompt_es":"Choose the correct passive form to complete each sentence.","mastery_tag":"grammar:b1-passive","explanation":"El uso de ''was'' indica que la acción ocurrió en el pasado y se usa con el participio pasado ''made''.","stimulus_en":"The cake ___ by my mother for my birthday.","options":[{"id":"o1","text":"was made"},{"id":"o2","text":"is made"},{"id":"o3","text":"makes"},{"id":"o4","text":"made"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","passive","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U16_B3_I3', 'ingles-b1-U16', 13, 'multiple_choice', 'Choose the correct passive form to complete each sentence.', '{"prompt_es":"Choose the correct passive form to complete each sentence.","mastery_tag":"grammar:b1-passive","explanation":"En oraciones pasivas, ''was'' se utiliza para indicar el pasado y seguido por el participio pasado ''produced''.","stimulus_en":"The new car ___ by the factory last year.","options":[{"id":"o1","text":"is produced"},{"id":"o2","text":"was produced"},{"id":"o3","text":"produced"},{"id":"o4","text":"produces"}],"correct_answer":"o2","complexity":2,"concept_tags":["grammar","passive","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U16_B3_I4', 'ingles-b1-U16', 14, 'multiple_choice', 'Choose the correct passive form to complete each sentence.', '{"prompt_es":"Choose the correct passive form to complete each sentence.","mastery_tag":"grammar:b1-passive","explanation":"El presente simple pasivo se forma con ''is'' y el participio pasado ''sung'', indicando una acción habitual.","stimulus_en":"This song ___ by many artists.","options":[{"id":"o1","text":"has been sung"},{"id":"o2","text":"sings"},{"id":"o3","text":"was sung"},{"id":"o4","text":"is sung"}],"correct_answer":"o4","complexity":2,"concept_tags":["grammar","passive","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U16_B3_I5', 'ingles-b1-U16', 15, 'multiple_choice', 'Choose the correct passive form to complete each sentence.', '{"prompt_es":"Choose the correct passive form to complete each sentence.","mastery_tag":"grammar:b1-passive","explanation":"El pasado pasivo se forma con ''was'' seguido del participio pasado ''sent'', indicando que la acción se completó en el pasado.","stimulus_en":"The letter ___ by John yesterday.","options":[{"id":"o1","text":"is sent"},{"id":"o2","text":"sent"},{"id":"o3","text":"was sent"},{"id":"o4","text":"sends"}],"correct_answer":"o3","complexity":2,"concept_tags":["grammar","passive","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;

