VALUES ('B1_U17_B1_I4', 'ingles-b1-U17', 4, 'multiple_choice', 'What is the term for a higher level of education after school?', '{"prompt_es":"What is the term for a higher level of education after school?","mastery_tag":"vocabulary:b1-education","explanation":"La universidad es una institución de educación superior donde los estudiantes pueden obtener títulos avanzados.","stimulus_en":"","options":[{"id":"o1","text":"University"},{"id":"o2","text":"Kindergarten"},{"id":"o3","text":"Primary"},{"id":"o4","text":"High School"}],"correct_answer":"o1","complexity":2,"concept_tags":["vocabulary","education","unit","grammar"]}')
