VALUES ('B1_U17_B1_I5', 'ingles-b1-U17', 5, 'multiple_choice', 'What do you call the action of learning a subject deeply?', '{"prompt_es":"What do you call the action of learning a subject deeply?","mastery_tag":"vocabulary:b1-education","explanation":"Dominar un tema significa tener un conocimiento profundo y completo sobre él.","stimulus_en":"","options":[{"id":"o1","text":"Review"},{"id":"o2","text":"Explore"},{"id":"o3","text":"Master"},{"id":"o4","text":"Forget"}],"correct_answer":"o3","complexity":2,"concept_tags":["vocabulary","education","unit","grammar"]}')
