VALUES ('B1_U17_B3_I3', 'ingles-b1-U17', 9, 'multiple_choice', 'What tense is commonly used to tell a story?', '{"prompt_es":"What tense is commonly used to tell a story?","mastery_tag":"writing:b1-stories","explanation":"El tiempo pasado simple se usa comúnmente para narrar eventos que ya ocurrieron.","stimulus_en":"","options":[{"id":"o1","text":"Present simple"},{"id":"o2","text":"Past simple"},{"id":"o3","text":"Future simple"},{"id":"o4","text":"Present continuous"}],"correct_answer":"o2","complexity":2,"concept_tags":["writing","stories","unit","grammar"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U17_B3_I4', 'ingles-b1-U17', 10, 'multiple_choice', 'What should you include to make the plot interesting?', '{"prompt_es":"What should you include to make the plot interesting?","mastery_tag":"writing:b1-stories","explanation":"Incluir giros en la trama mantiene a los lectores interesados y sorprendidos.","stimulus_en":"","options":[{"id":"o1","text":"Twists and turns"},{"id":"o2","text":"Long descriptions"},{"id":"o3","text":"Too many characters"},{"id":"o4","text":"Simple sentences"}],"correct_answer":"o1","complexity":2,"concept_tags":["writing","stories","unit","grammar"]}')
