INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U17_B3_I5', 'ingles-b1-U17', 11, 'multiple_choice', 'How can you connect the events in your story?', '{"prompt_es":"How can you connect the events in your story?","mastery_tag":"writing:b1-stories","explanation":"Las palabras de transición ayudan a los lectores a seguir el flujo de la historia y cómo los eventos se conectan.","stimulus_en":"","options":[{"id":"o1","text":"By listing them"},{"id":"o2","text":"Using transition words"},{"id":"o3","text":"Making them unrelated"},{"id":"o4","text":"Skipping events"}],"correct_answer":"o2","complexity":2,"concept_tags":["writing","stories","unit","grammar"]}')
