VALUES ('B1_U19_B1_I1', 'ingles-b1-U19', 1, 'multiple_choice', 'What is a common way to start an informal letter?', '{"prompt_es":"What is a common way to start an informal letter?","mastery_tag":"writing:b1-informal-letters","explanation":"En cartas informales, usamos saludos como ''Hi'' o ''Hey'' para dar un tono amigable.","stimulus_en":"","options":[{"id":"o1","text":"Dear"},{"id":"o2","text":"Hello"},{"id":"o3","text":"Hi"},{"id":"o4","text":"Hey"}],"correct_answer":"o3","complexity":2,"concept_tags":["writing","informal","letters","unit","grammar"]}')
