VALUES ('B1_U19_B1_I2', 'ingles-b1-U19', 2, 'multiple_choice', 'How should you end an informal letter?', '{"prompt_es":"How should you end an informal letter?","mastery_tag":"writing:b1-informal-letters","explanation":"Al finalizar una carta informal, ''Take care'' es una opción cálida y personal.","stimulus_en":"","options":[{"id":"o1","text":"Best wishes"},{"id":"o2","text":"Sincerely"},{"id":"o3","text":"Yours truly"},{"id":"o4","text":"Take care"}],"correct_answer":"o4","complexity":2,"concept_tags":["writing","informal","letters","unit","grammar"]}')
