VALUES ('B1_U19_B1_I4', 'ingles-b1-U19', 4, 'multiple_choice', 'What is a good way to ask about someone''s family in an informal letter?', '{"prompt_es":"What is a good way to ask about someone''s family in an informal letter?","mastery_tag":"writing:b1-informal-letters","explanation":"Preguntar ''How is your family?'' es una manera amable y cercana de interesarse por ellos.","stimulus_en":"","options":[{"id":"o1","text":"How is your family?"},{"id":"o2","text":"Tell me about your family."},{"id":"o3","text":"Do you have a family?"},{"id":"o4","text":"What is your family doing?"}],"correct_answer":"o1","complexity":2,"concept_tags":["writing","informal","letters","unit","grammar"]}')
