VALUES ('B1_U19_B1_I5', 'ingles-b1-U19', 5, 'multiple_choice', 'When writing an informal email, what should you avoid?', '{"prompt_es":"When writing an informal email, what should you avoid?","mastery_tag":"writing:b1-informal-letters","explanation":"En un email informal, es mejor evitar la formalidad y ser más relajado en el lenguaje.","stimulus_en":"","options":[{"id":"o1","text":"Using emojis"},{"id":"o2","text":"Being too formal"},{"id":"o3","text":"Short paragraphs"},{"id":"o4","text":"Friendly tone"}],"correct_answer":"o2","complexity":2,"concept_tags":["writing","informal","letters","unit","grammar"]}')
