INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U19_B2_I1', 'ingles-b1-U19', 6, 'fill_blanks', 'Complete the sentences by changing the form of the words in parentheses.

Base words: (1) POLLUTE, (2) SOLVE, (3) ACT, (4) CHANGE, (5) SUSTAIN', '{"prompt_es":"Complete the sentences by changing the form of the words in parentheses.\n\nBase words: (1) POLLUTE, (2) SOLVE, (3) ACT, (4) CHANGE, (5) SUSTAIN","mastery_tag":"vocabulary:b1-environment","main_text":"The (1)___ (POLLUTE) of our planet is a serious problem. We need to find (2)___ (SOLVE) for these environmental issues. Recycling is an important (3)___ (ACT) that everyone should practice. Climate change is causing serious (4)___ (CHANGE) in weather patterns. It''s essential to promote (5)___ (SUSTAIN) habits to protect our nature.","correct_answer":["POLLUTION","SOLUTIONS","ACTION","CHANGES","SUSTAINABLE"],"explanation":"(1) La palabra ''pollution'' es un sustantivo que se refiere al daño causado al medio ambiente.\n(2) La palabra ''solutions'' es el plural del sustantivo ''solution'', que significa ''respuesta'' a un problema.\n(3) La palabra ''action'' se refiere a un acto o acción que se toma, especialmente en un contexto de cambio ambiental.\n(4) La palabra ''changes'' es el plural de ''change'', y aquí se refiere a múltiples alteraciones en el clima.\n(5) La palabra ''sustainable'' es un adjetivo que describe prácticas que no agotan los recursos del medio ambiente.","complexity":2,"concept_tags":["vocabulary","environment","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U19_B3_I1', 'ingles-b1-U19', 7, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''happy'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''happy'':","mastery_tag":"grammar:b1-reported-speech","explanation":"En el discurso indirecto, el tiempo verbal cambia de presente a pasado. ''Am'' se convierte en ''was''.","stimulus_en":"She said, ''I am happy.''\n\nShe said that she was... ...","correct_answer":"happy.","example":"happy","complexity":2,"concept_tags":["grammar","reported","speech","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U19_B3_I2', 'ingles-b1-U19', 8, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''study'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''study'':","mastery_tag":"grammar:b1-reported-speech","explanation":"Cuando reportamos lo que alguien dijo, cambiamos ''must'' a ''had to'' en pasado.","stimulus_en":"He told her, ''You must study hard.''\n\nHe told her that she had to... ...","correct_answer":"study hard.","example":"study","complexity":2,"concept_tags":["grammar","reported","speech","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U19_B3_I3', 'ingles-b1-U19', 9, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''go'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''go'':","mastery_tag":"grammar:b1-reported-speech","explanation":"El verbo auxiliar ''can'' se transforma en ''could'' en el discurso indirecto.","stimulus_en":"They asked, ''Can we go to the park?''\n\nThey asked if they could... ...","correct_answer":"go to the park.","example":"go","complexity":2,"concept_tags":["grammar","reported","speech","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U19_B3_I4', 'ingles-b1-U19', 10, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''call'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''call'':","mastery_tag":"grammar:b1-reported-speech","explanation":"El tiempo futuro ''will'' se convierte en ''would'' y ''tomorrow'' se convierte en ''the next day''.","stimulus_en":"She said, ''I will call you tomorrow.''\n\nShe said that she would... ...","correct_answer":"call me the next day.","example":"call","complexity":2,"concept_tags":["grammar","reported","speech","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U19_B3_I5', 'ingles-b1-U19', 11, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''late'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''late'':","mastery_tag":"grammar:b1-reported-speech","explanation":"Usamos ''not to'' para transformar un mandato o consejo en indirecto.","stimulus_en":"He told me, ''Don''t be late.''\n\nHe told me not to be... ...","correct_answer":"late.","example":"late","complexity":2,"concept_tags":["grammar","reported","speech","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;

