INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U20_B1_I1', 'ingles-b1-U20', 1, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''stay'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''stay'':","mastery_tag":"grammar:b1-conditionals","explanation":"En esta oración, estamos usando la forma condicional que indica una situación real. ''If'' introduce la condición.","stimulus_en":"If it rains, I will stay home.\n\nI will stay home if it... ...","correct_answer":"rains.","example":"stay","complexity":2,"concept_tags":["grammar","conditionals","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U20_B1_I2', 'ingles-b1-U20', 2, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''pass'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''pass'':","mastery_tag":"grammar:b1-conditionals","explanation":"Aquí se está expresando una situación hipotética utilizando la segunda condicional.","stimulus_en":"If you studied hard, you would pass the exam.\n\nYou would pass the exam if you... ...","correct_answer":"studied hard.","example":"pass","complexity":2,"concept_tags":["grammar","conditionals","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U20_B1_I3', 'ingles-b1-U20', 3, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''drive'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''drive'':","mastery_tag":"grammar:b1-conditionals","explanation":"Esta oración usa ''had'' para señalar una situación irreal en el presente que se utiliza con la segunda condicional.","stimulus_en":"If I had a car, I would drive to work.\n\nI would drive to work if I... ...","correct_answer":"had a car.","example":"drive","complexity":2,"concept_tags":["grammar","conditionals","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U20_B1_I4', 'ingles-b1-U20', 4, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''boil'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''boil'':","mastery_tag":"grammar:b1-conditionals","explanation":"En este caso, se presenta una relación real y directa entre las dos acciones.","stimulus_en":"If you heat water, it will boil.\n\nWater will boil if you... ...","correct_answer":"heat it.","example":"boil","complexity":2,"concept_tags":["grammar","conditionals","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U20_B1_I5', 'ingles-b1-U20', 5, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''be'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''be'':","mastery_tag":"grammar:b1-conditionals","explanation":"Esta es una situación irreal, usando el verbo ''were'' para la segunda condicional.","stimulus_en":"If he were taller, he would be a basketball player.\n\nHe would be a basketball player if he... ...","correct_answer":"were taller.","example":"be","complexity":2,"concept_tags":["grammar","conditionals","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U20_B2_I1', 'ingles-b1-U20', 6, 'fill_blanks', 'Complete the sentences with the correct form of the word in parentheses.

Base words: (1) PERFORM, (2) CREATE, (3) AUDIENCE, (4) ACT, (5) MASTER', '{"prompt_es":"Complete the sentences with the correct form of the word in parentheses.\n\nBase words: (1) PERFORM, (2) CREATE, (3) AUDIENCE, (4) ACT, (5) MASTER","mastery_tag":"vocabulary:b1-entertainment","main_text":"The (1)___ of the movie was amazing. The director has great (2)___ in creating suspense. The (3)___ at the theater was full last night. Everyone enjoyed the (4)___ of the actors. It was a true (5)___ of art and entertainment.","correct_answer":["PERFORMANCE","CREATIVITY","AUDIENCE","ACTING","MASTERPIECE"],"explanation":"(1) Aquí se usa ''performance'' porque describe cómo se lleva a cabo la película.\n(2) Se necesita ''creativity'' para describir la habilidad de crear algo original.\n(3) La palabra ''audience'' se refiere a las personas que ven la película.\n(4) La forma ''acting'' se refiere a la acción de actuar en un filme o teatro.\n(5) Un ''masterpiece'' es una obra de arte excepcional, que refleja una gran habilidad.","complexity":2,"concept_tags":["vocabulary","entertainment","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U20_B3_I1', 'ingles-b1-U20', 7, 'multiple_choice', 'Choose the correct relative clause to complete each sentence.', '{"prompt_es":"Choose the correct relative clause to complete each sentence.","mastery_tag":"grammar:b1-relative-clauses","explanation":"Usamos ''which'' para referirnos a cosas o ideas, como un libro.","stimulus_en":"The book ___ you lent me was fascinating.","options":[{"id":"o1","text":"what"},{"id":"o2","text":"who"},{"id":"o3","text":"which"},{"id":"o4","text":"where"}],"correct_answer":"o3","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U20_B3_I2', 'ingles-b1-U20', 8, 'multiple_choice', 'Choose the correct relative clause to complete each sentence.', '{"prompt_es":"Choose the correct relative clause to complete each sentence.","mastery_tag":"grammar:b1-relative-clauses","explanation":"Usamos ''who'' para referirnos a personas, en este caso, una maestra.","stimulus_en":"She is the teacher ___ helped me with my project.","options":[{"id":"o1","text":"what"},{"id":"o2","text":"whose"},{"id":"o3","text":"who"},{"id":"o4","text":"that"}],"correct_answer":"o3","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U20_B3_I3', 'ingles-b1-U20', 9, 'multiple_choice', 'Choose the correct relative clause to complete each sentence.', '{"prompt_es":"Choose the correct relative clause to complete each sentence.","mastery_tag":"grammar:b1-relative-clauses","explanation":"''Where'' se usa para referirse a lugares, como una ciudad.","stimulus_en":"I visited the city ___ I grew up in.","options":[{"id":"o1","text":"when"},{"id":"o2","text":"where"},{"id":"o3","text":"who"},{"id":"o4","text":"that"}],"correct_answer":"o2","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
