INSERT INTO public.course_lessons (id, module_id, order_index, title, duration)
VALUES ('ingles-b1-U21', 'ingles-b1', 21, 'Unit 21', 600)
ON CONFLICT (id) DO UPDATE SET title = EXCLUDED.title, order_index = EXCLUDED.order_index;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U21_B1_I1', 'ingles-b1-U21', 1, 'multiple_choice', 'Choose the correct option to complete the sentences in reported speech.', '{"prompt_es":"Choose the correct option to complete the sentences in reported speech.","mastery_tag":"grammar:b1-reported-speech","explanation":"En el discurso indirecto, el tiempo del verbo cambia al pasado. ''Am going'' cambia a ''was going''.","stimulus_en":"She said, ''I am going to the party.''","options":[{"id":"o1","text":"She said she was going to the party."},{"id":"o2","text":"She said she is going to the party."},{"id":"o3","text":"She says she was going to the party."},{"id":"o4","text":"She said she goes to the party."}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","reported","speech","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U21_B1_I2', 'ingles-b1-U21', 2, 'multiple_choice', 'He asked, ''Do you like pizza?''', '{"prompt_es":"He asked, ''Do you like pizza?''","mastery_tag":"grammar:b1-reported-speech","explanation":"Cuando se reporta una pregunta, cambiamos el tiempo y la estructura, de ''do you like'' a ''if I liked''.","stimulus_en":"","options":[{"id":"o1","text":"He asked if I liked pizza."},{"id":"o2","text":"He asked if I like pizza."},{"id":"o3","text":"He asked did I like pizza."},{"id":"o4","text":"He asked whether I like pizza."}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","reported","speech","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U21_B1_I3', 'ingles-b1-U21', 3, 'multiple_choice', 'Choose the correct option to complete the sentences in reported speech.', '{"prompt_es":"Choose the correct option to complete the sentences in reported speech.","mastery_tag":"grammar:b1-reported-speech","explanation":"El futuro ''will'' cambia a ''would'' en el discurso indirecto.","stimulus_en":"They told me, ''We will finish the project tomorrow.''","options":[{"id":"o1","text":"They told me they finish the project tomorrow."},{"id":"o2","text":"They told me they finished the project tomorrow."},{"id":"o3","text":"They told me they would finish the project tomorrow."},{"id":"o4","text":"They told me they will finish the project tomorrow."}],"correct_answer":"o3","complexity":2,"concept_tags":["grammar","reported","speech","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U21_B1_I4', 'ingles-b1-U21', 4, 'multiple_choice', 'Choose the correct option to complete the sentences in reported speech.', '{"prompt_es":"Choose the correct option to complete the sentences in reported speech.","mastery_tag":"grammar:b1-reported-speech","explanation":"El presente perfecto ''have seen'' cambia a ''had seen'' en el discurso indirecto.","stimulus_en":"John said, ''I have seen that movie before.''","options":[{"id":"o1","text":"John said he has seen that movie before."},{"id":"o2","text":"John said he had seen that movie before."},{"id":"o3","text":"John said he saw that movie before."},{"id":"o4","text":"John said he was seeing that movie before."}],"correct_answer":"o2","complexity":2,"concept_tags":["grammar","reported","speech","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U21_B1_I5', 'ingles-b1-U21', 5, 'multiple_choice', 'Choose the correct option to complete the sentences in reported speech.', '{"prompt_es":"Choose the correct option to complete the sentences in reported speech.","mastery_tag":"grammar:b1-reported-speech","explanation":"El modal ''can'' cambia a ''could'' en el discurso indirecto.","stimulus_en":"Maria said, ''I can help you.''","options":[{"id":"o1","text":"Maria said she can help me."},{"id":"o2","text":"Maria said she could help me."},{"id":"o3","text":"Maria said she is able to help me."},{"id":"o4","text":"Maria says she could help you."}],"correct_answer":"o2","complexity":2,"concept_tags":["grammar","reported","speech","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U21_B2_I1', 'ingles-b1-U21', 6, 'fill_blanks', 'Fill in the blanks with the correct words to complete the conditional sentences.', '{"prompt_es":"Fill in the blanks with the correct words to complete the conditional sentences.","mastery_tag":"grammar:b1-conditionals","main_text":"If it ___ tomorrow, we will go to the beach.\n\nIf I ___ more time, I would travel around the world.\n\nIf you study hard, you ___ pass the exam.\n\nIf he ___ more careful, he would not make mistakes.\n\nIf they invite us, we ___ go to the party.","correct_answer":["rains","had","will","were","will"],"explanation":"(1) Usamos el presente simple ''rains'' porque es una condición real para el futuro.\n(2) Usamos ''had'' en la segunda condicional para situaciones irreales en el pasado.\n(3) Aquí usamos ''will'' porque es una condición real en el presente o futuro.\n(4) En la segunda condicional, usamos ''were'' para situaciones hipotéticas o irreales.\n(5) Usamos ''will'' porque es una respuesta a una posible acción futura.","complexity":2,"concept_tags":["grammar","conditionals","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U21_B3_I1', 'ingles-b1-U21', 7, 'multiple_choice', 'Choose the best option to complete each sentence about expressing opinions.', '{"prompt_es":"Choose the best option to complete each sentence about expressing opinions.","mastery_tag":"speaking:b1-expressing-opinions","explanation":"La respuesta correcta es ''connect'' porque el propósito de las redes sociales es unir a las personas.","stimulus_en":"I think social media is important because it helps people ______.","options":[{"id":"o1","text":"connect"},{"id":"o2","text":"disconnect"},{"id":"o3","text":"ignore"},{"id":"o4","text":"forget"}],"correct_answer":"o1","complexity":2,"concept_tags":["speaking","expressing","opinions","unit","grammar"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U21_B3_I2', 'ingles-b1-U21', 8, 'multiple_choice', 'Choose the best option to complete each sentence about expressing opinions.', '{"prompt_es":"Choose the best option to complete each sentence about expressing opinions.","mastery_tag":"speaking:b1-expressing-opinions","explanation":"La respuesta correcta es ''beneficial'' porque hace referencia a algo que es bueno para la salud.","stimulus_en":"In my opinion, going to the gym is ______ for a healthy life.","options":[{"id":"o1","text":"bad"},{"id":"o2","text":"unnecessary"},{"id":"o3","text":"beneficial"},{"id":"o4","text":"harmful"}],"correct_answer":"o3","complexity":2,"concept_tags":["speaking","expressing","opinions","unit","grammar"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U21_B3_I3', 'ingles-b1-U21', 9, 'multiple_choice', 'Choose the best option to complete each sentence about expressing opinions.', '{"prompt_es":"Choose the best option to complete each sentence about expressing opinions.","mastery_tag":"speaking:b1-expressing-opinions","explanation":"La respuesta correcta es ''bad'' porque se utiliza para expresar una opinión contraria.","stimulus_en":"I disagree with the idea that all technology is ______.","options":[{"id":"o1","text":"good"},{"id":"o2","text":"bad"},{"id":"o3","text":"useful"},{"id":"o4","text":"necessary"}],"correct_answer":"o2","complexity":2,"concept_tags":["speaking","expressing","opinions","unit","grammar"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U21_B3_I4', 'ingles-b1-U21', 10, 'multiple_choice', 'Choose the best option to complete each sentence about expressing opinions.', '{"prompt_es":"Choose the best option to complete each sentence about expressing opinions.","mastery_tag":"speaking:b1-expressing-opinions","explanation":"La respuesta correcta es ''free'' porque muchas opiniones proponen educación accesible para todos.","stimulus_en":"Many people believe that education should be ______ for everyone.","options":[{"id":"o1","text":"free"},{"id":"o2","text":"costly"},{"id":"o3","text":"optional"},{"id":"o4","text":"limited"}],"correct_answer":"o1","complexity":2,"concept_tags":["speaking","expressing","opinions","unit","grammar"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U21_B3_I5', 'ingles-b1-U21', 11, 'multiple_choice', 'Choose the best option to complete each sentence about expressing opinions.', '{"prompt_es":"Choose the best option to complete each sentence about expressing opinions.","mastery_tag":"speaking:b1-expressing-opinions","explanation":"La respuesta correcta es ''highlight'' porque significa dar importancia a un tema.","stimulus_en":"I feel that we need to ______ the importance of mental health.","options":[{"id":"o1","text":"ignore"},{"id":"o2","text":"underestimate"},{"id":"o3","text":"highlight"},{"id":"o4","text":"dismiss"}],"correct_answer":"o3","complexity":2,"concept_tags":["speaking","expressing","opinions","unit","grammar"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;

