INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U23_B1_I1', 'ingles-b1-U23', 1, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''cook'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''cook'':","mastery_tag":"grammar:b1-passive","explanation":"En la voz pasiva, el objeto de la oración activa se convierte en el sujeto de la oración pasiva.","stimulus_en":"The chef cooks the meal.\n\nThe meal is cooked... ...","correct_answer":"by the chef.","example":"cook","complexity":2,"concept_tags":["grammar","passive","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U23_B1_I2', 'ingles-b1-U23', 2, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''announce'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''announce'':","mastery_tag":"grammar:b1-passive","explanation":"En la voz pasiva, usamos ''will be'' + participio pasado para formar el futuro.","stimulus_en":"They will announce the results tomorrow.\n\nThe results will be announced... ...","correct_answer":"tomorrow.","example":"announce","complexity":2,"concept_tags":["grammar","passive","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U23_B1_I3', 'ingles-b1-U23', 3, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''grade'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''grade'':","mastery_tag":"grammar:b1-passive","explanation":"En este caso, usamos el presente continuo en voz pasiva.","stimulus_en":"The teacher is grading the papers.\n\nThe papers are being graded... ...","correct_answer":"by the teacher.","example":"grade","complexity":2,"concept_tags":["grammar","passive","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U23_B1_I4', 'ingles-b1-U23', 4, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''build'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''build'':","mastery_tag":"grammar:b1-passive","explanation":"Para la voz pasiva en pasado, usamos ''was/were'' + participio pasado.","stimulus_en":"They built the house last year.\n\nThe house was built... ...","correct_answer":"last year.","example":"build","complexity":2,"concept_tags":["grammar","passive","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U23_B1_I5', 'ingles-b1-U23', 5, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''clean'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''clean'':","mastery_tag":"grammar:b1-passive","explanation":"En la voz pasiva, el enfoque está en la acción realizada sobre el sujeto.","stimulus_en":"Someone will clean the room.\n\nThe room will be cleaned... ...","correct_answer":"by someone.","example":"clean","complexity":2,"concept_tags":["grammar","passive","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U23_B2_I1', 'ingles-b1-U23', 6, 'multiple_choice', 'What is the main cause of air pollution?', '{"prompt_es":"What is the main cause of air pollution?","mastery_tag":"vocabulary:b1-environment","explanation":"Los coches emiten gases que contaminan el aire, lo que causa la contaminación del aire.","stimulus_en":"","options":[{"id":"o1","text":"Cars"},{"id":"o2","text":"Trees"},{"id":"o3","text":"Birds"},{"id":"o4","text":"Water"}],"correct_answer":"o1","complexity":2,"concept_tags":["vocabulary","environment","unit","grammar"]}')
