INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U23_B2_I2', 'ingles-b1-U23', 7, 'multiple_choice', 'Which action helps reduce waste?', '{"prompt_es":"Which action helps reduce waste?","mastery_tag":"vocabulary:b1-environment","explanation":"Reciclar ayuda a reducir la cantidad de residuos al convertir materiales usados en nuevos productos.","stimulus_en":"","options":[{"id":"o1","text":"Recycling"},{"id":"o2","text":"Throwing away"},{"id":"o3","text":"Burning"},{"id":"o4","text":"Ignoring"}],"correct_answer":"o1","complexity":2,"concept_tags":["vocabulary","environment","unit","grammar"]}')
