INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U23_B2_I3', 'ingles-b1-U23', 8, 'multiple_choice', 'What does climate change affect?', '{"prompt_es":"What does climate change affect?","mastery_tag":"vocabulary:b1-environment","explanation":"El cambio climático afecta a los animales al alterar sus hábitats y patrones de migración.","stimulus_en":"","options":[{"id":"o1","text":"Animals"},{"id":"o2","text":"Gravity"},{"id":"o3","text":"Light"},{"id":"o4","text":"Sound"}],"correct_answer":"o1","complexity":2,"concept_tags":["vocabulary","environment","unit","grammar"]}')
