INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U23_B2_I4', 'ingles-b1-U23', 9, 'multiple_choice', 'Why is deforestation a problem?', '{"prompt_es":"Why is deforestation a problem?","mastery_tag":"vocabulary:b1-environment","explanation":"La deforestación destruye los hogares de muchos animales, llevando a la disminución de especies.","stimulus_en":"","options":[{"id":"o1","text":"It creates more land."},{"id":"o2","text":"It provides wood."},{"id":"o3","text":"It destroys habitats."},{"id":"o4","text":"It makes rain."}],"correct_answer":"o3","complexity":2,"concept_tags":["vocabulary","environment","unit","grammar"]}')
