VALUES ('B1_U23_B2_I5', 'ingles-b1-U23', 10, 'multiple_choice', 'What can you do to protect nature?', '{"prompt_es":"What can you do to protect nature?","mastery_tag":"vocabulary:b1-environment","explanation":"Plantar árboles ayuda a limpiar el aire y proporciona hábitats para muchas especies.","stimulus_en":"","options":[{"id":"o1","text":"Plant trees"},{"id":"o2","text":"Ignore pollution"},{"id":"o3","text":"Use plastic"},{"id":"o4","text":"Waste water"}],"correct_answer":"o1","complexity":2,"concept_tags":["vocabulary","environment","unit","grammar"]}')
