VALUES ('B1_U23_B3_I1', 'ingles-b1-U23', 11, 'multiple_choice', 'Choose the correct option to complete each sentence using the passive voice.', '{"prompt_es":"Choose the correct option to complete each sentence using the passive voice.","mastery_tag":"grammar:b1-passive","explanation":"En la voz pasiva, usamos ''was'' para indicar que la acción fue realizada en el pasado.","stimulus_en":"The report ___ by the team last week.","options":[{"id":"o1","text":"is completed"},{"id":"o2","text":"was completed"},{"id":"o3","text":"has completed"},{"id":"o4","text":"completed"}],"correct_answer":"o2","complexity":2,"concept_tags":["grammar","passive","unit"]}')
