VALUES ('B1_U23_B3_I4', 'ingles-b1-U23', 14, 'multiple_choice', 'Choose the correct option to complete each sentence using the passive voice.', '{"prompt_es":"Choose the correct option to complete each sentence using the passive voice.","mastery_tag":"grammar:b1-passive","explanation":"Se utiliza ''is adopted'' porque la política se aplica en el presente.","stimulus_en":"The new policy ___ by the board this month.","options":[{"id":"o1","text":"is adopted"},{"id":"o2","text":"was adopted"},{"id":"o3","text":"has been adopted"},{"id":"o4","text":"adopted"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","passive","unit"]}')
