INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U24_B1_I2', 'ingles-b1-U24', 2, 'multiple_choice', 'Choose the correct answer for each question about conditionals.', '{"prompt_es":"Choose the correct answer for each question about conditionals.","mastery_tag":"grammar:b1-conditionals","explanation":"Se utiliza ''would'' en la segunda condicional para situaciones hipotéticas.","stimulus_en":"If I had a million dollars, I ___ travel the world.","options":[{"id":"o1","text":"will"},{"id":"o2","text":"would"},{"id":"o3","text":"can"},{"id":"o4","text":"might"}],"correct_answer":"o2","complexity":2,"concept_tags":["grammar","conditionals","unit"]}')
