INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U24_B1_I3', 'ingles-b1-U24', 3, 'multiple_choice', 'Choose the correct answer for each question about conditionals.', '{"prompt_es":"Choose the correct answer for each question about conditionals.","mastery_tag":"grammar:b1-conditionals","explanation":"En este caso, ''will'' se usa para acciones futuras que dependen de una condición.","stimulus_en":"If I see her at the party, I ___ tell her the news.","options":[{"id":"o1","text":"will"},{"id":"o2","text":"would"},{"id":"o3","text":"should"},{"id":"o4","text":"could"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","conditionals","unit"]}')
